<?php/** * * @author: xaboy<365615158@qq.com> * @day: 2017/11/11 */namespace app\admin\model\user;use traits\ModelTrait;use basic\ModelBasic;/** * 用户管理 model * Class User * @package app\admin\model\user */class UserLevel extends ModelBasic{    use ModelTrait;    /**获取器     * @param $value     * @return mixed     */    public function getStatusAttr($value)    {        $status = [0=>'禁用',1=>'正常'];        return $status[$value];    }    public static function getList($where){        $Model = new self();        $data =  $Model::setWherePage($Model,$where)->page((int)$where['page'],(int)$where['limit'])->select();        $count = $Model::setWherePage($Model,$where)->count();        return compact('data','count');    }}