{extend name="public/modal-frame"}{block name="head_top"}<link href="{__ADMIN_PATH}module/wechat/news/css/style.css" type="text/css" rel="stylesheet"><link href="{__FRAME_PATH}css/plugins/chosen/chosen.css" rel="stylesheet"><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/ueditor/ueditor.config.js"></script><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/ueditor/ueditor.all.js"></script><script src="{__ADMIN_PATH}frame/js/ajaxfileupload.js"></script><script src="{__ADMIN_PATH}plug/validate/jquery.validate.js"></script><script src="{__FRAME_PATH}js/plugins/chosen/chosen.jquery.js"></script>{/block}{block name="content"}<div class="panel">   <div class="panel-body">       <form class="form-horizontal" id="signupForm">           <div class="form-group">               <div class="col-md-12">                   <div class="input-group">                       <span class="input-group-addon">标题</span>                       {if condition="$style eq 'create'"}                       <input maxlength="64" placeholder="请在这里输入标题" name="title" class="layui-input" id="title" value="">                       {elseif condition="$style eq 'edit'"/}                       <input maxlength="64" placeholder="请在这里输入标题" name="title" class="layui-input" id="title" value="{$announce.title}">                       {elseif condition="$style eq 'view'"/}                       <input maxlength="64" placeholder="请在这里输入标题" name="title" class="layui-input" id="title" value="{$announce.title}" readonly>                       {/if}                       <input type="hidden" name="id" value="">                   </div>               </div>           </div>           <div class="form-group">               <div class="col-md-12">                   <div class="input-group" data-select="$select">                       <span class="input-group-addon">版块分类</span>                       {if condition="$style eq 'create'"}                       <select class="layui-select layui-input" name="fid" id="fid">                           <option value="">请选择版块分类</option>                           {volist name="select" id="vo"}                           <option value="{$vo.id}">{$vo.name}</option>                           {/volist}                       </select>                       {elseif condition="$style eq 'edit'"/}                       <select class="layui-select layui-input" name="fid" id="fid" value="{$announce.fid}">                           <option value="">不选择</option>                           {volist name="select" id="vo"}                           {if condition="$vo.id eq $announce.fid"}                           <option value="{$vo.id}" selected>{$vo.name}</option>                           {else/}                           <option value="{$vo.id}">{$vo.name}</option>                           {/if}                           {/volist}                       </select>                       {elseif condition="$style eq 'view'"/}                       <select class="layui-select layui-input" name="fid" id="fid" disabled value="{$announce.fid}">                           <option value="">不选择</option>                           {volist name="select" id="vo"}                           {if condition="$vo.id eq $announce.fid"}                           <option value="{$vo.id}" selected>{$vo.name}</option>                           {else/}                           <option value="{$vo.id}">{$vo.name}</option>                           {/if}                           {/volist}                       </select>                       {/if}                   </div>               </div>           </div>           <div class="form-group">               <div class="col-md-12">                   <div class="input-group">                       <span class="input-group-addon">二级分类</span>                       {if condition="$style eq 'create'"}                       <select class="layui-select layui-input" name="class_id" id="class_id">                           <option value="">不选择</option>                       </select>                       {elseif condition="$style eq 'edit'"/}                       <select class="layui-select layui-input" name="class_id" id="class_id" value="{$announce.class_id}">                           <option value="">不选择</option>                           {volist name="class" id="v"}                           {if condition="$v.id eq $announce.class_id"}                           <option value="{$v.id}" selected>{$v.name}</option>                           {else/}                           <option value="{$v.id}">{$v.name}</option>                           {/if}                           {/volist}                       </select>                       {elseif condition="$style eq 'view'"/}                       <select class="layui-select layui-input" name="class_id" id="class_id" disabled value="{$announce.class_id}">                           <option value="">不选择</option>                           {volist name="class" id="v"}                           {if condition="$v.id eq $announce.class_id"}                           <option value="{$v.id}" selected>{$v.name}</option>                           {else/}                           <option value="{$v.id}">{$v.name}</option>                           {/if}                           {/volist}                       </select>                       {/if}                   </div>               </div>           </div>           {if condition="$style eq 'create'"}           <div class="form-group">               <div class="col-md-12">                   <div class="input-group">                       <span class="input-group-addon" style="height: 38px;border-right: 1px solid #E5E6E7;">是否即时发送</span>                       <div style="display: flex;align-items: center;height: 38px;">                           <label for="yes_send" style="margin-bottom: 0;margin-left: 20px;margin-right: 5px;">是</label>                           <input type="radio" name="send" id="yes_send" value="yes" style="margin-top: 0;" checked>                           <label for="no_send" style="margin-bottom: 0;margin-left: 20px;margin-right: 5px;">自定义</label>                           <input class="layui-form-radio" id="no_send" type="radio" name="send" value="no" style="margin-top: 0;">                       </div>                   </div>               </div>           </div>           {/if}           {if condition="$style eq 'create'"}           <div class="form-group" id="send_time" style="display: none;">               <div class="col-md-12">                   <div class="input-group">                       <span class="input-group-addon">发布时间</span>                       <input type="text" class="layui-input" id="start_time" readonly placeholder="请选择发布时间">                   </div>               </div>           </div>           {elseif condition="$style eq 'edit'"/}           <div class="form-group" id="send_time">               <div class="col-md-12">                   <div class="input-group">                       <span class="input-group-addon">发布时间</span>                       <input type="text" class="layui-input" id="start_time" readonly placeholder="请选择发布时间">                   </div>               </div>           </div>           {elseif condition="$style eq 'view'"/}           <div class="form-group" id="send_time">               <div class="col-md-12">                   <div class="input-group">                       <span class="input-group-addon">发布时间</span>                       <input type="text" class="layui-input" id="start_time" readonly placeholder="请选择发布时间">                   </div>               </div>           </div>           {/if}           <div class="form-group">               <div class="col-md-12">                   <label style="color:#aaa">公告内容</label>                   {if condition="$style eq 'create'"}                   <textarea type="text/plain" id="myEditor" style="width:100%;"></textarea>                   {elseif condition="$style eq 'edit'"/}                   <textarea type="text/plain" id="myEditor" style="width:100%;">{$announce.content}</textarea>                   {elseif condition="$style eq 'view'"/}                   <textarea type="text/plain" id="myEditor" style="width:100%;">{$announce.content}</textarea>                   {/if}               </div>           </div>           <div class="form-actions">               <div class="row">                   <div class="col-md-offset-4 col-md-9">                       <button type="button" class="btn btn-w-m btn-info save_news">保存</button>                   </div>               </div>           </div>       </form>   </div></div>{/block}{block name="script"}    <script>      function add0(m){return m<10?'0'+m:m }      function format(shijianchuo)      {//shijianchuo是整数，否则要parseInt转换        var time = new Date(shijianchuo);        var y = time.getFullYear();        var m = time.getMonth()+1;        var d = time.getDate();        var h = time.getHours();        var mm = time.getMinutes();        var s = time.getSeconds();        return y+'-'+add0(m)+'-'+add0(d)+' '+add0(h)+':'+add0(mm)+':'+add0(s);      }    </script>    {if condition="$style eq 'create'"}    <script>      layui.use('laydate', function(){        var laydate = layui.laydate;        //执行一个laydate实例        laydate.render({          elem: '#start_time', //指定元素          type:'datetime',          value:new Date        });      });      /**       * 提交图文       * */      $('.save_news').on('click',function(){        var startTime = $('#start_time').val();        console.log($('#fid').val());        var list = {};        list.title = $('#title').val();/* 标题 */        list.author_uid = $('#author').val();/* 作者 */        list.fid = $('#fid option:selected').val();/* 版块分类 */        list.class_id = $('#class_id option:selected').val();/* 分类 */        list.start_time = dataToStamp(startTime);/* 开始时间 */        list.content = getContent();/* 内容 */        var Expression = /http(s)?:\/\/([\w-]+\.)+[\w-]+(\/[\w- .\/?%&=]*)?/;        var objExp=new RegExp(Expression);        if(list.title == ''){          $eb.message('error','请输入标题');          return false;        }        if(list.author_uid == ''){          $eb.message('error','请输入作者');          return false;        }        if(list.fid == ''){          $eb.message('error','请选择版块分类');          return false;        }        if(list.start_time == ''){          $eb.message('error','请输入开始时间');          return false;        }        if(list.content == ''){          $eb.message('error','请输入内容');          return false;        }        var data = {};        $.ajax({          url:"{:Url('add_announce')}",          data:list,          type:'post',          dataType:'json',          success:function(re){            if(re.code == 200){              data[re.data] = list;              $('.type-all>.active>.new-id').val(re.data);              $eb.message('success',re.msg);              setTimeout(function (e) {                parent.$(".J_iframe:visible")[0].contentWindow.location.reload();                parent.layer.close(parent.layer.getFrameIndex(window.name));              },600)            }else{              $eb.message('error',re.msg);            }          }        })      });    </script>    {elseif condition="$style eq 'edit'"/}    <script>      var startTime = "{$announce.start_time}";      var startTimeDate = format(startTime*1000);      layui.use('laydate', function(){        var laydate = layui.laydate;        //执行一个laydate实例        laydate.render({          elem: '#start_time', //指定元素          type:'datetime',          value: startTimeDate        });      });      /**       * 提交图文       * */      $('.save_news').on('click',function(){        var startTime = $('#start_time').val();        console.log($('#fid').val());        var list = {};        list.id = "{$announce.id}";        list.title = $('#title').val();/* 标题 */        list.author_uid = $('#author').val();/* 作者 */        list.fid = $('#fid option:selected').val();/* 版块分类 */        list.class_id = $('#class_id option:selected').val();/* 分类 */        list.start_time = dataToStamp(startTime);/* 开始时间 */        list.content = getContent();/* 内容 */        var Expression = /http(s)?:\/\/([\w-]+\.)+[\w-]+(\/[\w- .\/?%&=]*)?/;        var objExp=new RegExp(Expression);        if(list.title == ''){          $eb.message('error','请输入标题');          return false;        }        if(list.author_uid == ''){          $eb.message('error','请输入作者');          return false;        }        if(list.fid == ''){          $eb.message('error','请选择版块分类');          return false;        }        if(list.start_time == ''){          $eb.message('error','请输入开始时间');          return false;        }        if(list.content == ''){          $eb.message('error','请输入内容');          return false;        }        var data = {};        $.ajax({          url:"{:Url('edit_announce')}",          data:list,          type:'post',          dataType:'json',          success:function(re){            if(re.code == 200){              data[re.data] = list;              $('.type-all>.active>.new-id').val(re.data);              $eb.message('success',re.msg);              setTimeout(function (e) {                parent.$(".J_iframe:visible")[0].contentWindow.location.reload();                parent.layer.close(parent.layer.getFrameIndex(window.name));              },600)            }else{              $eb.message('error',re.msg);            }          }        })      });    </script>    {elseif condition="$style eq 'view'"/}    <script>      var startTime = "{$announce.start_time}";      var startTimeDate = format(startTime*1000);      $("#start_time").val(startTimeDate);    </script>    {/if}    <script>      $("#fid").change(function () {        $.ajax({          url:"{:Url('select_class')}",          data:{id:$("#fid").val()},          type:'post',          dataType:'json',          success:function(res){            if(res.code == 200){              var optionHtml = '<option value="">不选择</option>';              for(var i in res.data){                optionHtml += '<option value='+res.data[i].id+'>'+res.data[i].name+'</option>'              }              $("#class_id").html(optionHtml)            }else{            }          }        })      })      $('input[type=radio][name=send]').change(function() {        if (this.value === 'yes') {          $("#send_time").hide();          $('#start_time').val(new Date);        }        else if (this.value === 'no') {          $("#send_time").show();          $('#start_time').val("");        }      });    </script><script>  var ue = UE.getEditor('myEditor',{    autoHeightEnabled: false,    initialFrameHeight: 400,    wordCount: false,    maximumWords: 100000  });            /**            * 获取编辑器内的内容            * */            function getContent() {              return (ue.getContent());            }            function hasContent() {                return (UM.getEditor('myEditor').hasContents());            }            function createFrame(title,src,opt){                opt === undefined && (opt = {});                return layer.open({                    type: 2,                    title:title,                    area: [(opt.w || 700)+'px', (opt.h || 650)+'px'],                    fixed: false, //不固定                    maxmin: true,                    moveOut:false,//true  可以拖出窗外  false 只能在窗内拖                    anim:5,//出场动画 isOutAnim bool 关闭动画                    offset:'auto',//['100px','100px'],//'auto',//初始位置  ['100px','100px'] t[ 上 左]                    shade:0,//遮罩                    resize:true,//是否允许拉伸                    content: src,//内容                    move:'.layui-layer-title'                });            }            function changeIMG(index,pic){                $(".image_img").css('background-image',"url("+pic+")");                $(".active").css('background-image',"url("+pic+")");                $('#image_input').val(pic);            };            /**             * 上传图片             * */            $('.upload_span').on('click',function (e) {//                $('.upload').trigger('click');                createFrame('选择图片','{:Url('widget.images/index')}?fodder=image');            })            /**             * 编辑器上传图片             * */            $('.edui-icon-image').on('click',function (e) {//                $('.upload').trigger('click');                createFrame('选择图片','{:Url('widget.images/index')}?fodder=image');            })            $('.article-add ').on('click',function (e) {                var num_div = $('.type-all').children('div').length;                if(num_div > 7){                  $eb.message('error','一组图文消息最多可以添加8个');                  return false;                }                var url = "/public/system/module/wechat/news/images/image.png";                html = '';                html += '<div class="news-item transition active news-image" style=" margin-bottom: 20px;background-image:url('+url+')">'                    html += '<input type="hidden" name="new_id" value="" class="new-id">';                    html += '<span class="news-title del-news">x</span>';                html += '</div>';                $(this).siblings().removeClass("active");                $(this).before(html);            })            $(document).on("click",".del-news",function(){                $(this).parent().remove();            })            $(document).ready(function() {                var config = {                    ".chosen-select": {},                    ".chosen-select-deselect": {allow_single_deselect: true},                    ".chosen-select-no-single": {disable_search_threshold: 10},                    ".chosen-select-no-results": {no_results_text: "沒有找到你要搜索的分类"},                    ".chosen-select-width": {width: "95%"}                };                for (var selector in config) {                    $(selector).chosen(config[selector])                }            })                function dataToStamp(data) {              if(data === ""){                return "";              }else {                var str = data.replace(/-/g,'/');                var dataTime = new Date(str);                dataTime = Date.parse(dataTime);                dataTime = dataTime / 1000;                return dataTime;              }            }        </script>    {if condition="$style eq 'view'"/}    <script>      ue.ready(function () {        ue.setDisabled()      });    </script>    {/if}{/block}