{extend name="public/modal-frame"}{block name="head_top"}<link href="{__ADMIN_PATH}module/wechat/news/css/style.css" type="text/css" rel="stylesheet"><link href="{__FRAME_PATH}css/plugins/chosen/chosen.css" rel="stylesheet"><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/ueditor/ueditor.config.js"></script><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/ueditor/ueditor.all.js"></script><script src="{__ADMIN_PATH}frame/js/ajaxfileupload.js"></script><script src="{__ADMIN_PATH}plug/validate/jquery.validate.js"></script><script src="{__FRAME_PATH}js/plugins/chosen/chosen.jquery.js"></script>{/block}{block name="content"}    <div class="panel">        <div class="panel-body">            <form class="form-horizontal" id="signupForm">                <div class="form-group">                    <div class="col-md-12">                        <div class="input-group">                            <span class="input-group-addon">禁言原因</span>                            <input maxlength="64" placeholder="请在这里禁言原因" name="name" class="layui-input" id="name" value="{$info['name']}">                            <input type="hidden" name="id" id="id" value="{$info['id']}">                        </div>                    </div>                </div>                <div class="form-group">                    <div class="col-md-12">                        <div class="input-group">                            <span class="input-group-addon">序号</span>                            <input maxlength="8" placeholder="请输入序号" name="sort" class="layui-input" id="sort" value="{$info['sort']}">                        </div>                    </div>                </div>                <div class="form-actions">                    <div class="row">                        <div class="col-md-offset-4 col-md-9">                            <button type="button" class="btn btn-w-m btn-info save_news">保存</button>                        </div>                    </div>                </div>            </form>        </div>    </div>{/block}{block name="script"}<script>            /**            * 提交图文            * */            $('.save_news').on('click',function(){                var list = {};                list.name = $('#name').val();/* 标题 */                list.id = $('#id').val();/* 作者 */                list.sort =$('#sort').val();/* 内容 */                if(list.name == ''){                    $eb.message('error','禁言理由');                    return false;                }                if(list.sort == ''){                    $eb.message('error','序号');                    return false;                }                var data = {};                $.ajax({                    url:"{:Url('save')}",                    data:list,                    type:'post',                    dataType:'json',                    success:function(re){                        if(re.code == 200){                            data[re.data] = list;                            $('.type-all>.active>.new-id').val(re.data);                            $eb.message('success',re.msg);                            setTimeout(function (e) {                                parent.$(".J_iframe:visible")[0].contentWindow.location.reload();//                                parent.layer.close(parent.layer.getFrameIndex(window.name));                            },600)                        }else{                            $eb.message('error',re.msg);                        }                    }                })            });        </script>{/block}